import { useContext, useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';

import { DispatchesContext } from "../../App";

import DefaultButton from "../DefaultButton";

function Form({ isEdit, originData }) {
  const navigate = useNavigate();
  const { id } = useParams();

  const { onCreate, onEdit, onDelete } = useContext(DispatchesContext);

  const [userInfo, setUserInfo] = useState({
    name: "",
    title: "",
  });

  const inputChangeHandler = (event) => {
    setUserInfo((prev) => {
      return {
        ...prev,
        [event.target.id]: event.target.value,
      };
    });
  };

  const onSubmit = (event) => {
    event.preventDefault();

    if (isEdit) {
      onEdit(id, userInfo);
    } else {
      onCreate(userInfo.name, userInfo.title);
    }

    setUserInfo({
      name: "",
      title: "",
    });

    navigate("/");
  };

  useEffect(() => {
    if (isEdit) {
      setUserInfo(originData);
    }
  }, [isEdit, originData]);

  const onDeleteTodo = () => {
    onDelete(id);

    navigate("/", { replace: true });
  };

  return (
      <Box
       onSubmit={onSubmit}
      component="form"
      sx={{
        '& > :not(style)': { m: 1, width: '25ch' },
      }}
      noValidate
      autoComplete="off"
    >
      <div>
        <label htmlFor="name">유저이름</label>
     ]
        <TextField type="text"
          id="name"
          value={userInfo.name || ""}
          onChange={inputChangeHandler} id="filled-basic" label="Filled" variant="filled" />
      </div>
      <div>
        <label htmlFor="title">할일제목</label>
        <TextField type="text"
          id="title"
          value={userInfo.title || ""}
          onChange={inputChangeHandler} id="filled-basic" label="Filled" variant="filled" />
      </div>
      <DefaultButton type="submit">저장하기</DefaultButton>
      {isEdit && <DefaultButton onClick={onDeleteTodo} variant={'outlined'}>삭제하기</DefaultButton>}
      </Box>
  );
}
export default Form;
